// Bootstrap CSS
import "bootstrap/dist/css/bootstrap.min.css";
import React, { useState } from 'react';
import Home from "./Home.jsx";
import Programs from "./Programs.jsx";
import Rebook from "./Rebook.jsx";
import Confirmation from "./Confirmation.jsx";
import Locations from "./Locations.jsx";
import Laundry from "./Laundry.jsx";
import Contact from './Contact.jsx';
import { Routes, Route } from 'react-router-dom';

function NavBar() {

    // const [selectedComponent, setSelectedComponent] = useState('home');

    // const handleNavbarItemClick = (item) => {
    //     setSelectedComponent(item);
    // };

    // let currentComponent = null;

    // switch (selectedComponent) {
    //     case 'home':
    //     currentComponent = <Home />;
    //     break;
    //     case 'programs':
    //     currentComponent = <Programs />;
    //     break;
    //     case 'rebook':
    //     currentComponent = <Rebook />;
    //     break;
    //     case 'confirmation':
    //     currentComponent = <Confirmation />;
    //     break;
    //     case 'locations':
    //     currentComponent = <Locations />;
    //     break;
    //     case 'laundry':
    //     currentComponent = <Laundry />;
    //     break;
    //     case 'contact':
    //     currentComponent = <Contact />;
    //     break;
    //     default:
    //     currentComponent = <Home />;
    // }

    return(
        <div>
            <nav class="navbar navbar-expand-lg bg-body-tertiary">
                <div class="container-fluid">
                <a style={{textDecoration: 'none'}} href="index.html"><div class="d-flex flex-row mb-3">
                    <h1 style={{color: 'purple'}}>Hope</h1>
                    <h1 style={{color: 'orange'}}>Shelter</h1>
                </div></a>
                <button class="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#navbarNav" aria-controls="navbarNav" aria-expanded="false" aria-label="Toggle navigation">
                    <span class="navbar-toggler-icon"></span>
                </button>
                <div class="collapse navbar-collapse d-flex flex-row-reverse" id="navbarNav">
                    <ul class="navbar-nav">
                    <li onClick={() => handleNavbarItemClick('home')} class="nav-item">
                        <a class="nav-link active" aria-current="page" href="index.html">Home</a>
                    </li>
                    <li onClick={() => handleNavbarItemClick('programs')} class="nav-item">
                        <a class="nav-link active" href="book.html">Programs</a>
                    </li>
                    <li class="nav-item">
                        <a class="nav-link active" href="services.html">Book</a>
                    </li>
                    <li class="nav-item">
                        <a class="nav-link active" href="locations.html">Locations</a>
                    </li>
                    <li class="nav-item">
                        <a class="nav-link active" href="about.html">Laundry</a>
                    </li>
                    <li class="nav-item">
                        <a class="nav-link active" href="contact.html">Contact</a>
                    </li>
                    <li>
                        <button style={{background: 'orange', color: 'white', border: 'none', borderRadius: '5px', padding:"5px"}}> Donate </button>
                    </li>
                    </ul>
                </div>
                </div>
            </nav>
            <div style={{height: '2px', width: '100%', background: 'purple'}}></div>
        </div>
    );
}

export default NavBar;
